// import './estilosdoprojeto.css';
import React, { useState, useEffect } from "react";
import axios from "axios";
import Formulario from './components/Formulario';

function App() {
  const [reunioes, setReunioes] = useState([]);

  useEffect(() => {
    async function fetchData() {
      console.log("here")
      try {
        const response = await fetch("https://service-aluguel-salas.onrender.com/reuniao");
        if (response.ok) {
          const data = await response.json();
          console.log("JSON recebido:", data);
          setReunioes(data);
        } else {
          console.error("Erro na resposta da solicitação GET");
        }
      } catch (error) {
        console.error("Erro ao buscar as reuniões:", error);
      }
      console.log("here3")
    }
    fetchData();
  }, []);
  console.log(reunioes)

  //TODO FUNÇÃO APAGAR
  function deletar(id)
  {
    console.log("deletar id", id)
    // Defina a URL da API
    const apiUrl = "https://service-aluguel-salas.onrender.com/reuniao";

    // ID da reunião que você deseja excluir
    const reuniaoId = id;

    // Configuração do Axios para fazer a solicitação DELETE
    axios
      .delete(`${apiUrl}/${reuniaoId}`)
      .then((response) => {
        if (response.status === 200) {
          console.log("Reunião excluída com sucesso.");
          alert('Reunião cancelada com sucesso!')
          window.location.reload();
        } else {
          console.error("Erro na resposta da solicitação DELETE");
        }
      })
      .catch((error) => {
        console.error("Erro ao excluir a reunião:", error);
      });

  }

  return (
    <>
    <h1 style={{ marginLeft: '30%' }}>Aluguel de salas de reunião</h1>
    <div className="container">

    <Formulario />

      <table>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Loja</th>
            <th>Grau</th>
            <th>Sala</th>
            <th>Data Inicial</th>
            <th>Data Final</th>
          </tr>
        </thead>
        <tbody>
          {reunioes.map((reuniao) => (
            <tr key={reuniao.id}>
              <td>{reuniao.nome}</td>
              <td>{reuniao.loja}</td>
              <td>{reuniao.grau}</td>
              <td>{reuniao.sala}</td>
              <td>{reuniao.dataInicial}</td>
              <td>{reuniao.dataFinal}</td>
              <td><button onClick={()=> deletar(reuniao.id)}>Cancelar</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
    </>
  );
}

export default App;
