import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [reunioes, setReunioes] = useState([]);

  useEffect(() => {
    async function fetchData() {
      console.log("here")
      try {
        const response = await fetch("https://service-aluguel-salas.onrender.com/reuniao");
        if (response.ok) {
          const data = await response.json();
          console.log("JSON recebido:", data);
          setReunioes(data);
        } else {
          console.error("Erro na resposta da solicitação GET");
        }
      } catch (error) {
        console.error("Erro ao buscar as reuniões:", error);
      }
      console.log("here3")
    }
    fetchData();
  }, []);
  console.log(reunioes)

  //TODO FUNÇÃO APAGAR
  function deletar(id)
  {
    console.log("deletar id", id)
    // Defina a URL da API
    const apiUrl = "https://service-aluguel-salas.onrender.com/reuniao";

    // ID da reunião que você deseja excluir
    const reuniaoId = id;

    // Configuração do Axios para fazer a solicitação DELETE
    axios
      .delete(`${apiUrl}/${reuniaoId}`)
      .then((response) => {
        if (response.status === 200) {
          console.log("Reunião excluída com sucesso.");
        } else {
          console.error("Erro na resposta da solicitação DELETE");
        }
      })
      .catch((error) => {
        console.error("Erro ao excluir a reunião:", error);
      });

  }


  return (
    <div>

      <h1>Adicionar Reunião</h1>
      <form>
        <div>
          <label htmlFor="nome">Nome:</label>
          <input
            type="text"
            id="nome"
            name="nome"
            value={formData.nome}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="grau">Grau:</label>
          <input
            type="text"
            id="grau"
            name="grau"
            value={formData.grau}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="loja">Loja:</label>
          <input
            type="text"
            id="loja"
            name="loja"
            value={formData.loja}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="sala">Sala:</label>
          <select id="sala" name="sala" value={formData.sala} onChange={handleChange}>
            <option value={1}>Sala 1</option>
            <option value={2}>Sala 2</option>
            <option value={3}>Sala 3</option>
            <option value={4}>Sala 4</option>
            <option value={5}>Sala 5</option>
            <option value={6}>Sala 6</option>
            <option value={7}>Sala 7</option>
            <option value={8}>Sala 8</option>
            <option value={9}>Sala 9</option>
          </select>
        </div>
        <div>
          <label htmlFor="dataInicial">Data Inicial:</label>
          <input
            type="datetime-local"
            id="dataInicial"
            name="dataInicial"
            value={formData.dataInicial}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="dataFinal">Data Final:</label>
          <input
            type="datetime-local"
            id="dataFinal"
            name="dataFinal"
            value={formData.dataFinal}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Adicionar Reunião</button>
      </form>


      <h2>Reuniões Agendadas</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Loja</th>
            <th>Grau</th>
            <th>Sala</th>
            <th>Data Inicial</th>
            <th>Data Final</th>
          </tr>
        </thead>
        <tbody>
          {reunioes.map((reuniao) => (
            <tr key={reuniao.id}>
              <td>{reuniao.id}</td>
              <td>{reuniao.nome}</td>
              <td>{reuniao.loja}</td>
              <td>{reuniao.grau}</td>
              <td>{reuniao.sala}</td>
              <td>{reuniao.dataInicial}</td>
              <td>{reuniao.dataFinal}</td>
              <td><button onClick={()=> deletar(reuniao.id)}>Cancelar</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
